{
    $string = '';
    if(is_array($post))
    {
        foreach($post as $item)
        {
            if(is_array($item))
                $string .= getPostString($item);
            else
                $string .= $item;
        }
    }
    else
    {
        $string = $post;
    }

    return $string;
}
