{
	char masked_raider[] = "Alived";

	char *jimmy = masked_raider;

	printf("masked_raider is %s,jimmy is %s\n", masked_raider, jimmy);

	masked_raider[0] = 'd';
	masked_raider[1] = 'e';
	masked_raider[2] = 'a';
	masked_raider[3] = 'd';
	masked_raider[4] = 'e';
